package org.example.Node.blockchain;

import org.example.Utils.rsa.RsaGeneration;

import java.math.BigInteger;
import java.security.PublicKey;
import java.util.ArrayList;
import java.util.List;
import java.util.Random;

public class Genesis {
    static Block createBlock(){
        Random random = new Random();
        BigInteger modulus = new BigInteger("");
        BigInteger exponent = new BigInteger("65537");
        var transaction = new Transaction(null, RsaGeneration.generatePublicKeyFromModulus(modulus, exponent), 10000, random.nextInt());
        List<Transaction> transactionPool = new ArrayList<>();
        transactionPool.add(transaction);
        return new Block(transactionPool, "0");
    }
}
